import React, { useState, useEffect } from 'react'

const ModalExample = (props) => {

    const [revele, setRevele] = useState(false);

    useEffect(() => {
        setRevele(props.revele)
    }, [props.revele])

    return(
        <>
            <div>
                {revele ? <p>This is modal</p> : ""}
            </div>
        </>
    )
}

export default ModalExample
