import React from 'react'
import { Button, Col, Container, Row } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux'
import {logout} from '../../actions/login'

const Profile = () => {

    const user = useSelector(state => state.user);
    const dispatch = useDispatch();

    const onDelete = () => {
        fetch(`http://localhost:8080/users/${user.id}`, {method: 'delete'})
        .then(response => {
            if (response.ok) {
                return response.json();
            }else {
              throw response;
          }
        }).then(response => {
          dispatch(logout());
          console.log(response);
          alert("Account deleted");
        }).catch(error => {
          if (error instanceof Promise) {
            error.json().then((body) => {
              console.log(body.error);
            });
          }else {
            alert(error)
          }
        })
    }
    

    return(
            <Container>
                <Row>
                    <h3 className="my-5">Welcome to your Profile {user.firstName} {user.lastName}</h3>
                </Row>
                <Row>
                  <Col>
                    
                  </Col>
                </Row>
                <Row>
                  <Col><Button onClick={confirm("Do you wish to delete this account ?") ? onDelete : null}>Update Profile</Button></Col>
                  <Col><Button onClick={onDelete}>Delete Profile</Button></Col>
                    
                </Row>
            </Container>
        
    )
}

export default Profile
