import React from 'react'
import { Carousel } from 'primereact/carousel';


const HomeCarousel = (props) => {
    const responsiveOptions = [
        {
            breakpoint: '1024px',
            numVisible: 3,
            numScroll: 3
        },
        {
            breakpoint: '600px',
            numVisible: 2,
            numScroll: 2
        },
        {
            breakpoint: '480px',
            numVisible: 1,
            numScroll: 1
        }
    ];
    const images = props.images;
    function itemTemplate(imageURL) {
        return(
            <div className="product-item">
                <div className="product-item-content">
                    <div className="p-mb-3">
                        <img src={imageURL} alt={imageURL} className="product-image h-100" />
                    </div>
                </div>
            </div>
        )
    }

    
    return(
        <div className="content-section">
            <div className="card">
                <Carousel value={images} numVisible={1} numScroll={1} itemTemplate={itemTemplate} autoplayInterval={4000} responsiveOptions={responsiveOptions} circular />
            </div>
        </div>  
    )
}

export default HomeCarousel
