import React, { useState } from 'react'
import './SearchBar.css'

const products = [
    'tooth paste',
    'tooth brush',
    'mouth wash',
    'dental floss',
    'mouth guard',
]

const SearchBar = () => {
    const [searchValue, setSearchValue] = useState("the search value")

    const handleInputChange = (event) => {
        setSearchValue (event.target.value)
    }

    const handleClearClick = () => {
        setSearchValue("")
    }

    const shouldDisplayClearButton = searchValue.length > 0
    console.log(searchValue)

    return (
        <div>
            <input type="text" value={searchValue} onChange={handleInputChange}></input>
            {shouldDisplayClearButton && <button onClick={handleClearClick}>Clear</button>}
            {products.map( (product) => {
                return <li key={product}>{product}</li>
            })}
        </div>
    )
}
export default SearchBar
