import React, { useEffect, useState } from 'react'

const Article = ({match}) => {

    const [article, setArticle] = useState({});
    const [highestBid, setHighestBid] = useState({})

    useEffect(() => {
        // Get article
        fetch(
            `http://localhost:8080/articles/${match.params.id}`
        ).then((response) => {
            if (response.ok) {
                return response.json();
            }
        }).then((data) => {
            setArticle(data.data);
        })
        // Get highest bid for article
        fetch(
            `http://localhost:8080/articles/bid/${match.params.id}`
        ).then((response) => {
            if (response.ok) {
                return response.json();
            }
        }).then((data) => {
            setHighestBid(data.data);
        })
    }, [ match.params.id]);

    return(
        <div className="text-center">
            <h1>{article.title}</h1>
            <img className="my-3" src={article.photoURL} alt="article pic" />
            <h4>Estimation : {article.estimation} â‚¬</h4>
            <h5>{highestBid == null ? "No bids yet" : `Highest bid : ${highestBid.bidAmount}`} </h5>
        </div>
    )
}

export default Article
