import React, { useState } from 'react'
import ArticleCard from './article/article'
import './SearchBar.css'



const SearchBar = (props) => {
    const [searchValue, setSearchValue] = useState("")

    const handleInputChange = (event) => {
        setSearchValue (event.target.value)
    }

    const handleClearClick = () => {
        setSearchValue("")
    }

    const filteredProducts = props.searchedList.filter( (product) => {
        return product.title.toLowerCase().includes(searchValue.toLowerCase()) || product.description.toLowerCase().includes(searchValue.toLowerCase())
    })

    const shouldDisplayClearButton = searchValue.length > 0
    console.log(searchValue)

    return (
        <div>
            <input type="text" value={searchValue} onChange={handleInputChange}></input>
            {shouldDisplayClearButton && <button onClick={handleClearClick}>Clear</button>}
            <ul>
                {/* {filteredProducts.map( (product) => {
                    return <li key={product.id}>{product.title}</li>
                })} */}
                <ArticleCard articles={filteredProducts}/>
            </ul>
        </div>
    )
}
export default SearchBar
