import React from 'react'
import { Carousel } from 'primereact/carousel';
import ArticleCard from '../articleCard/articleCard';



const ArticleCarousel = (props) => {
    const responsiveOptions = [
        {
            breakpoint: '1024px',
            numVisible: 3,
            numScroll: 3
        },
        {
            breakpoint: '600px',
            numVisible: 2,
            numScroll: 2
        },
        {
            breakpoint: '480px',
            numVisible: 1,
            numScroll: 1
        }
    ];
    const articles = props.articles;
    console.log(articles)
    const itemTemplate = (article) => {
        return(
            <ArticleCard article={article}/>
        )
    }
    return(
        <div className="card">
            <Carousel value={this.state.products} numVisible={3} numScroll={3} responsiveOptions={this.responsiveOptions}
                itemTemplate={this.productTemplate} header={<h5>Basic</h5>} />
        </div>
    )
}

export default ArticleCarousel
