import React from 'react'
import { Carousel } from 'primereact/carousel';
import ArticleCard from '../articleCard/articleCard';



const ArticleCarousel = (props) => {
    const responsiveOptions = [
        {
            breakpoint: '1024px',
            numVisible: 3,
            numScroll: 3
        },
        {
            breakpoint: '600px',
            numVisible: 2,
            numScroll: 2
        },
        {
            breakpoint: '480px',
            numVisible: 1,
            numScroll: 1
        }
    ];
    const articles = props.articles;
    console.log(articles)
    const itemTemplate = (articles) => {
        return(
            <div>
                {articles == null ? "Loading..." : articles.map( (article) => {
                    return(
                        <ArticleCard key={article.ID} article={article}/>
                    )})}
            </div>
            
            )
        
    }
    return(
        <div className="content-section">
            <div className="card">
                <Carousel value={articles} autoplayInterval={3000} responsiveOptions={responsiveOptions}
                    itemTemplate={itemTemplate} header={<h5>Basic</h5>} />
            </div>
        </div>
        
        
    )
}

export default ArticleCarousel
