import React, { useEffect, useState } from 'react'
import './article.css'



const ArticleCard = (props) => {

    

    return(
        <div>
            <ul>
                {props.articles == null ? "Loading..." : props.articles.map( (article) => {
                    return (
                        <li key={article.ID} class="align-center">
                            <img  class="p-2" src={article.photoURL} alt="testing"></img> 
                            <h4>{article.title}</h4>
                            <p>{article.description}</p>
                        </li>
                    )
                })}
            </ul>
            
        </div>
    )
}


export default ArticleCard
