import React from 'react'
import { Formik, Form as FromikForm, Field } from 'formik';
import 'bootstrap/dist/css/bootstrap.min.css'
import * as Yup from 'yup';

const SignUpSchema = Yup.object().shape({
   email: Yup.string().email('Invalid email').required('Required'),
   password: Yup.string()

     .min(2, 'Too Short!')

     .max(50, 'Too Long!')

     .required('Required'),
})

export const SignUpForm = () => (

    <div>
 
      <Formik
 
        initialValues={{
 
          firstName: '',
 
          lastName: '',
 
          email: '',
 
        }}
 
        validationSchema={SignUpSchema}
 
        onSubmit={values => {
 
          // same shape as initial values
 
          console.log(values);
 
        }}
 
      >
 
        {({ errors, touched }) => (
 
          <FromikForm> 
            <Field className="form-control my-2" name="email" type="email" placeHolder="Email" />
            {errors.email && touched.email ? <div>{errors.email}</div> : null}

            <Field className="form-control my-2" placeHolder="Password" name="password" />
            {errors.lastName && touched.lastName ? (<div>{errors.lastName}</div>) : null}

            <button className="btn btn-info justify-content-center" type="submit">Submit</button>
 
          </FromikForm>
 
        )}
 
      </Formik>
 
    </div>
 
  );

  export default SignUpForm
