import React, { useEffect } from 'react'
import { Formik, Form as FromikForm, Field } from 'formik';
import 'bootstrap/dist/css/bootstrap.min.css'
import * as Yup from 'yup';
import { useSelector } from 'react-redux';

const SignInSchema = Yup.object().shape({
   email: Yup.string().email('Invalid email').required('Required'),
   password: Yup.string()
     .min(2, 'Too Short!')
     .max(18, 'Too Long!')
     .required('Required'),
})

export const SignUpForm = () => {
    const isLoggedIn = useSelector(state => state.login)
    useEffect(() => {
        
        
    }, [])
    return(<div>
      <Formik
        initialValues={{
            email: '',
            password:'',
        }}
        validationSchema={SignInSchema}
        onSubmit={values => {
            fetch(`http://localhost:8080/user/email`, {method: 'POST',body: JSON.stringify(values)})
            .then(response => {
                if (response.ok) {
                    return response.json();
                }
                else throw response.json();
            }).then(response => {
                console.log(response.data)
            }).catch(error => {
                console.log(error.value.error)
            })
        }}
      >
        {({ errors, touched }) => (
          <FromikForm> 
            <Field className="form-control my-2" name="email" type="email" placeHolder="Email" />
            {errors.email && touched.email ? <div>{errors.email}</div> : null}

            <Field className="form-control my-2" name="password" type="password" placeHolder="Password" />
            {errors.password && touched.password ? (<div>{errors.password}</div>) : null}

            <button className="btn btn-info pull-right" type="submit">Submit</button>
 
          </FromikForm>
        )}
      </Formik>
    </div>)
}

  export default SignUpForm
