import React from 'react'
import HomeCarousel from './homeCarousel/homeCarousel'
import {useSelector, useDispatch} from 'react-redux'
import {login, logout} from '../../actions/login'
import { Button } from 'react-bootstrap'
const images = [`${process.env.PUBLIC_URL}/images/DrouotHome2.jpg`,`${process.env.PUBLIC_URL}/images/DrouotHome3.jpg`]

// Just to have a home page
const Home = () => {
    const loginState = useSelector(state => state.login)
    console.log("this is the login state :", loginState)
    const dispatch = useDispatch()
    return(
        <>
        <div className="text-center">
        <h1>Drouot</h1>
        <HomeCarousel images={images}/>
        </div>
        {loginState ? <h1>Is logged in</h1> : <h1>Is not logged in</h1>}
        {loginState ? <Button onClick={() => dispatch(logout())}>log out</Button> : <Button onClick={() => dispatch(login())}>login</Button>}
        
        
        </>
    )
}

export default Home
