import React from 'react'
import { Carousel } from 'primereact/carousel';
import './articleCarousel.css'
import ArticleCard from '../articleCard/articleCard';


const ArticleCarousel = (props) => {
    const responsiveOptions = [
        {
            breakpoint: '1024px',
            numVisible: 3,
            numScroll: 3
        },
        {
            breakpoint: '600px',
            numVisible: 2,
            numScroll: 2
        },
        {
            breakpoint: '480px',
            numVisible: 1,
            numScroll: 1
        }
    ];
    const articles = props.articles;
    function itemTemplate(article) {
        return(
            <ArticleCard article={article}/>
        )
    }

    
    return(
        <div className="content-section">
            <div className="card">
                <Carousel value={articles} numVisible={4} numScroll={1} itemTemplate={itemTemplate} autoplayInterval={3000} responsiveOptions={responsiveOptions} circular />
            </div>
        </div>  
    )
}

export default ArticleCarousel
