import React, { useEffect, useState } from 'react'


const ArticleCard = () => {

    const [articles, setArticles] = useState(null)

    useEffect(() => {
        fetch('http://localhost:8080/articles').then(response => {
            if (response.ok) {
                return response.json();
            }
        }).then( data => {
            setArticles(data)
        })
    }, [])

    return(
        <div>
            {articles == nul ? "Lodaing..." : articles[0]  }
        </div>
    )
}


export default ArticleCard
