import React from 'react'
import './articleCard.css'
import {Link} from 'react-router-dom'



const ArticleCard = (props) => {
    var articles = props.filtered
    return (
        <div className="row text-center">
            {articles == null ? "Loading..." : articles.map( (article) => {
                return(
                    
                        <div className="col-12 col-sm-6 col-md-4 col-lg-3 my-3" key={article.ID}>
                            <Link to={`/article/${article.ID}`}>
                                <div className="card border-white bg-light h-100">
                                    <div className="card-body">
                                        <img className="card-img-top" src={article.photoURL} alt="test"/>
                                        <h5 className="card-title text-center">{article.title}</h5>
                                        <p className="card-text text-justify">{article.description}</p>
                                        <p className="card-text text-center">estim. {article.estimation} â‚¬</p>
                                    </div>
                                </div>
                            </Link>
                        </div>
                )
            })}
        </div>
    )
}


export default ArticleCard
