import React from 'react'
import { Carousel } from 'primereact/carousel';
import ArticleCard from '../articleCard/articleCard';



const ArticleCarousel = (props) => {
    const responsiveOptions = [
        {
            breakpoint: '1024px',
            numVisible: 3,
            numScroll: 3
        },
        {
            breakpoint: '600px',
            numVisible: 2,
            numScroll: 2
        },
        {
            breakpoint: '480px',
            numVisible: 1,
            numScroll: 1
        }
    ];
    const articles = props.articles;
    console.log(articles)
    const itemTemplate = (article) => {
        console.log(article)
        return(
            <div>
                <img className="card-img-top" src={article.photoURL} alt="test"/>
                <h5 className="card-title text-center">{article.title}</h5>
                <p className="card-text text-justify">{article.description}</p>
                <p className="card-text text-center">estim. {article.estimation} â‚¬</p>
            </div>

        )
    }
    return(
        <div className="content-section">
            <div className="card">
                <Carousel value={articles} autoplayInterval={3000} responsiveOptions={responsiveOptions}
                    itemTemplate={itemTemplate} header={<h5>Basic</h5>} />
            </div>
        </div>
        
        
    )
}

export default ArticleCarousel
