import React, { useEffect, useState } from 'react'
import ArticleCarousel from '../articleCarousel/articleCarousel';


const Article = (props) => {

    const [articles, setArticles] = useState([]);
    const [article, setArticle] = useState({});
    const [highestBid, setHighestBid] = useState({})
    const [errorMessage, setErrorMessage] = useState("")

    useEffect(() => {
            
        // Get article
        fetch(
            `http://localhost:8080/articles/${props.match.params.id}`
        ).then((response) => {
            if (response.ok) {
                return response.json();
            }
        },(error) => {
            return error;
        }).catch((error) => {
            console.log(error.error)
        }).then((data) => {
            setArticle(data.data);
            console.log(article)
        },(error) => {
            return error;
        }).catch((error) => {
            console.log(error.error)
        })
        // Get highest bid for article
        fetch(
            `http://localhost:8080/articles/bid/${props.match.params.id}`
        ).then((response) => {
            if (response.ok) {
                return response.json();
            }
            throw response;
        }).then((data) => {
            setHighestBid(data.data);
        }).catch(error => {
            error.json().then((body) => {
                setErrorMessage(body.error);
            });
        })
        // Get all articles
        fetch(
            `http://localhost:8080/auctions/articles/${article.auctionID}`
        ).then(response => {
            if (response.ok) {
                return response.json();
            }
        }).then( (data) => {
            setArticles(data.data);
            console.log("articles :",data.data);
        })
    }, [ props.match.params.id]);

    return(
        <div>
        <div className="">
            <div className="row ">
            <div class="col-4 text-center">
                    <h1>{article.title}</h1>
                    <img className="my-3" src={article.photoURL} alt="article pic" />
                </div>
                <div class="col-8 ">
                <h4>Estimation : {article.estimation} €</h4>
                <h5>Highest bid : {errorMessage !== "" ? "none" : highestBid.bidAmount} </h5>
                <button className="btn btn-info btn-lg px-5 py-3 m-3 ">Bid</button>
                </div>
                
                
            </div>
            
        </div>
        <ArticleCarousel articles={articles} />
        </div>
    )
}

export default Article
