import React, { useEffect, useState } from 'react'
import {Button, Form, Modal} from 'react-bootstrap'


const Login = () => {
    const [user, setUser] = useState({
        email: "",
        password: ""
    })

    const handleChange = (event) => {
        if (event.target.id === "email") {
            let email = event.target.value
            setUser(prevState => {
                return { ...prevState, email: email}
            })
        }
        if (event.target.id === "password") {
            let password = event.target.value
            setUser(prevState => {
                return { ...prevState, password: password}
            })
        }
    }

    useEffect(()=> {
        console.log("user state",user)
    })


    

    const onSignIn = () => {

    }

    const [showSignIn, setShowSignIn] = useState(false);
    const [showSignUp, setShowSignUp] = useState(false);

    const handleCloseSignIn = () => {
      setShowSignIn(false);
      setUser({
          email: "",
          password: ""
      })
    }
    const handleCloseSignUp = () => {
      setShowSignUp(false);
      setUser({
          email: "",
          password: ""
      })
    }
    const handleSignInShow = () => {setShowSignIn(true);setShowSignUp(false)}
    const handleSignUpShow = () => {setShowSignIn(false);setShowSignUp(true)}
  
    return (
      <>
        <Button variant="default" onClick={handleSignInShow}>
          Connexion
        </Button>

        {/* This is the modal for Sign in */}
        <Modal show={showSignIn} onHide={handleCloseSignIn}>
          <Modal.Header closeButton>
            <Modal.Title>Connexion</Modal.Title>
          </Modal.Header>
          <Modal.Body>
          <Form>
            <Form.Group controlId="email">
                <Form.Label>Email address</Form.Label>
                <Form.Control onChange={handleChange} type="email" placeholder="Enter email" />
                <Form.Text className="text-muted">
                We'll never share your email with anyone else.
                </Form.Text>
            </Form.Group>

            <Form.Group controlId="password">
                <Form.Label>Password</Form.Label>
                <Form.Control onChange={handleChange} type="password" placeholder="Password" />
            </Form.Group>
            <Form.Group controlId="formBasicCheckbox">
                <Form.Check type="checkbox" label="Check me out" />
            </Form.Group>
            <Button variant="primary" type="submit" onSubmit={onSignIn}>
                Submit
            </Button>
            </Form>
          </Modal.Body>
          <Modal.Footer>
          <p>Need an account ?</p>
            <Button variant="default" onClick={handleSignUpShow}>
              Sign-up
            </Button>
          </Modal.Footer>
        </Modal>

        {/* This is the modal for Sign up */}
        <Modal show={showSignUp} onHide={handleCloseSignUp}>
          <Modal.Header closeButton>
            <Modal.Title>Sign Up</Modal.Title>
          </Modal.Header>
          <Modal.Body>
          <Form>
            <Form.Group controlId="email">
                <Form.Label>Email address</Form.Label>
                <Form.Control onChange={handleChange} type="email" placeholder="Enter email" />
                <Form.Text className="text-muted">
                We'll never share your email with anyone else.
                </Form.Text>
            </Form.Group>

            <Form.Group controlId="password">
                <Form.Label>Password</Form.Label>
                <Form.Control onChange={handleChange} type="password" placeholder="Password" />
            </Form.Group>
            <Button variant="primary" type="submit" onSubmit={onSignIn}>
                Submit
            </Button>
            </Form>
          </Modal.Body>
          <Modal.Footer>
            <p>Already have an account ?</p>
            <Button variant="default" onClick={handleSignInShow}>
              Sign-in
            </Button>
          </Modal.Footer>
        </Modal>
      </>
    );
  
