import React from 'react'
import './article.css'



const Article = (props) => {
    var articles = props.filtered
    return (
        <div>
            {articles == null ? "Loading..." : articles.map( (article) => {
                return (
                    <div class="col-12 col-sm-6 col-md-4 col-lg-3 my-3" key={article.id}>
                        <div class="card border-white bg-light h-100">
                            <div class="card-body">
                                <img class="card-img-top" src={article.photoURL} alt="test"/>
                                <h5 class="card-title text-center">{article.title}</h5>
                                <p class="card-text text-justify">{article.description}</p>
                                <p class="card-text text-center">estim. {article.estimation} â‚¬</p>
                            </div>
                        </div>
                    </div>
                )
            })}
        </div>
        
        
    )
}


export default Article
