import React from 'react'
import { Carousel } from 'primereact/carousel';
import ArticleCard from '../articleCard/articleCard';



const ArticleCarousel = (props) => {
    var responsiveOptions = [
        {
            breakpoint: '1024px',
            numVisible: 3,
            numScroll: 3
        },
        {
            breakpoint: '600px',
            numVisible: 2,
            numScroll: 2
        },
        {
            breakpoint: '480px',
            numVisible: 1,
            numScroll: 1
        }
    ];
    const articles = props.articles;
    const itemTemplate = (article) => {
        return(
            <ArticleCard article={article}/>
        )
    }
    return(
        <div>
            <Carousel value={articles} numVisible={3} numScroll={3} responsiveOptions={responsiveOptions} itemTemplate={itemTemplate} orientation="horizontal"/>
        </div>
    )
}

export default ArticleCarousel
