import React, { useEffect, useState } from 'react'
import { Button } from 'react-bootstrap';
import { useSelector } from 'react-redux';
import Login from '../../../../login/loginModal';
import ArticleCarousel from '../articleCarousel/articleCarousel';


const Article = (props) => {
    const user = useSelector(state => state.user);
    const [articles, setArticles] = useState([]);
    const [article, setArticle] = useState({});
    const [highestBid, setHighestBid] = useState({});
    const [errorMessage, setErrorMessage] = useState("");
    const articleId = props.match.params.id;

    useEffect(() => {
            
        // Get article
        fetch(
            `http://localhost:8080/articles/${articleId}`
        ).then((response) => {
            if (response.ok) {
                return response.json();
            }else {
                throw response;
            }
            
        }).then((data) => {
            setArticle(data.data);
            // Get highest bid for article
        fetch(
            `http://localhost:8080/articles/bid/${data.data.ID}`
        ).then((response) => {
            if (response.ok) {
                return response.json();
            }else{
                throw response;
            }
        }).then((data) => {
            setHighestBid(data.data);
        }).catch(error => {
            error.json().then((body) => {
                setErrorMessage(body.error);
                setHighestBid(article.initialOffering)
                return body.error;
            });
        })
            // Get all articles
            fetch(
                `http://localhost:8080/auctions/articles/${data.data.auctionID}`
            ).then(response => {
                if (response.ok) {
                    return response.json();
                }else {
                    throw response;
                }
            }).then( (data) => {
                setArticles(data.data);
            })
        }).catch(error => {
            error.json().then((body) => {
                setErrorMessage(body.error);
                return body.error;
            });
        })
        
        
    }, [article.initialOffering, articleId]);
    const createBid = () => {
        const bid = {
            bidDate:new Date(), 
            userId:user.id, 
            articleId:article.ID, 
            bidAmount:(errorMessage !== "" ? (Number.parseInt(article.initialOffering) + 10) : (Number.parseInt(highestBid.bidAmount) + 10) ),
        }
        fetch(`http://localhost:8080/bids`, {method: 'POST', body: JSON.stringify(bid)})
            .then(response => {
                if (response.ok) {
                    return response.json();
                }else {
                    throw response.json();
                }
            }).then(data => {
                setHighestBid(data.data)
            }).catch(error => {
                if (error instanceof Promise) {
                  error.then(error => {
                    // for dev purposes only
                    console.log(error);
                    alert(error)
                  })
                }else {
                  // for dev purposes
                  console.log("not promise")
                  alert(error)
                }
              })
    }

    return(
        <div>
            <div className="row mx-auto">
                <div className="col-2">
                </div>
                <div className="col-4 text-center">
                    <h1 className="text-center">{article.title}</h1>
                    <img className="w-75 my-3" src={article.photoURL} alt="article pic" />
                </div>
                <div className="col-2 text-center my-auto">
                    <h4>Estimation : {article.estimation} €</h4>
                    <h5>Highest bid : {errorMessage !== "" ? article.initialOffering : highestBid.bidAmount} </h5>
                    {user.id === "" ? <Login></Login> : <Button variant="info" onClick={createBid}></Button>}
                    
                </div>
        </div>
        <ArticleCarousel articles={articles} />
        </div>
    )
}

export default Article
