import React, { useState } from 'react'
// import ArticleCard from './article/article'
import './SearchBar.css'



const Articles = (props) => {
    const [searchValue, setSearchValue] = useState("")

    const handleInputChange = (event) => {
        setSearchValue (event.target.value)
    }

    const handleClearClick = () => {
        setSearchValue("")
    }

    const filtered = props.searchedList.filter( (product) => {
        return product.title.toLowerCase().includes(searchValue.toLowerCase()) || product.description.toLowerCase().includes(searchValue.toLowerCase())
    })

    const shouldDisplayClearButton = searchValue.length > 0
    console.log(searchValue)
    
    return (
        <div class="container mx-auto text-center">
            <div class="input-group mb-3">
                <input type="search" class="form-control" value={searchValue} onChange={handleInputChange} placeholder="Rechercher un objet"></input>                
                <div class="input-group-append">
                    {shouldDisplayClearButton && <button class="btn btn-outline-secondary" type="button" onClick={handleClearClick}>Clear</button>}
                </div>
            </div>
            <Article filtered={filtered}/>
            <div class="row text-center">
                {filtered == null ? "Loading..." : filtered.map( (article) => {
                    return (
                        <div class="col-12 col-sm-6 col-md-4 col-lg-3 my-3">
                            <div class="card border-white bg-light h-100">
                                <div class="card-body">
                                    <img class="card-img-top" src={article.photoURL} alt="test"/>
                                    <h5 class="card-title text-center">{article.title}</h5>
                                    <p class="card-text text-justify">{article.description}</p>
                                    <p class="card-text text-center">estim. {article.estimation} â‚¬</p>
                                </div>
                            </div>
                        </div>
                    )
                })}
            </div>
        </div>
            
    )
}
export default Articles
